class WalletComponent {
  constructor(walletService, accountService) {
    this.walletService = walletService;
    this.accountService = accountService;
    this.wallet = null;
    this.walletSummary = null;
    this.recentTransactions = [];
    this.cryptoHoldings = [];
    this.myQRCodes = [];
    this.accounts = [];
    // UI State
    this.activeTab = 'overview';
    this.showTopUpModal = false;
    this.showWithdrawModal = false;
    this.showSendModal = false;
    this.showQRModal = false;
    this.showCryptoModal = false;
    this.isLoading = false;
    // Form data
    this.topUpForm = {
      amount: 0,
      sourceAccountId: '',
      pin: ''
    };
    this.withdrawForm = {
      amount: 0,
      destinationAccountId: '',
      pin: ''
    };
    this.sendForm = {
      recipientWallet: '',
      amount: 0,
      description: '',
      pin: ''
    };
    this.qrForm = {
      amount: 0,
      description: ''
    };
    this.cryptoForm = {
      symbol: '',
      amount: 0,
      action: 'buy',
      pin: ''
    };
    this.availableCryptos = [];
    this.subscriptions = new Subscription();
  }
  ngOnInit() {
    this.loadWalletData();
    this.loadAccounts();
    this.loadAvailableCryptos();
  }
  ngOnDestroy() {
    this.subscriptions.unsubscribe();
  }
  loadWalletData() {
    this.isLoading = true;
    // Load wallet info
    this.subscriptions.add(this.walletService.getWallet().subscribe({
      next: wallet => {
        this.wallet = wallet;
        this.loadWalletSummary();
        this.loadRecentTransactions();
        this.loadCryptoHoldings();
        this.loadMyQRCodes();
      },
      error: error => {
        if (error.message.includes('not found')) {
          // Wallet doesn't exist, offer to create one
          this.createWallet();
        }
        this.isLoading = false;
      }
    }));
  }
  createWallet() {
    this.subscriptions.add(this.walletService.createWallet().subscribe({
      next: wallet => {
        this.wallet = wallet;
        this.loadWalletData();
      },
      error: () => this.isLoading = false
    }));
  }
  loadWalletSummary() {
    this.subscriptions.add(this.walletService.getWalletSummary().subscribe({
      next: summary => {
        this.walletSummary = summary;
        this.isLoading = false;
      }
    }));
  }
  loadRecentTransactions() {
    this.subscriptions.add(this.walletService.getTransactions(0, 5).subscribe({
      next: response => {
        this.recentTransactions = response.transactions;
      }
    }));
  }
  loadCryptoHoldings() {
    this.subscriptions.add(this.walletService.getCryptoHoldings().subscribe({
      next: holdings => {
        this.cryptoHoldings = holdings;
      }
    }));
  }
  loadMyQRCodes() {
    this.subscriptions.add(this.walletService.getMyQRCodes().subscribe({
      next: qrCodes => {
        this.myQRCodes = qrCodes;
      }
    }));
  }
  loadAccounts() {
    this.subscriptions.add(this.accountService.getAccounts().subscribe({
      next: accounts => {
        this.accounts = accounts;
      }
    }));
  }
  loadAvailableCryptos() {
    this.subscriptions.add(this.walletService.getAvailableCryptos().subscribe({
      next: cryptos => {
        this.availableCryptos = cryptos;
      }
    }));
  }
  // Tab Management
  setActiveTab(tab) {
    this.activeTab = tab;
  }
  // Top Up Wallet
  openTopUpModal() {
    this.showTopUpModal = true;
    this.topUpForm = {
      amount: 0,
      sourceAccountId: '',
      pin: ''
    };
  }
  closeTopUpModal() {
    this.showTopUpModal = false;
  }
  topUpWallet() {
    if (this.topUpForm.amount <= 0 || !this.topUpForm.sourceAccountId) {
      return;
    }
    const request = {
      walletId: this.wallet.id,
      amount: this.topUpForm.amount,
      sourceAccountId: this.topUpForm.sourceAccountId,
      currency: 'USD',
      pin: this.topUpForm.pin
    };
    this.subscriptions.add(this.walletService.topUpWallet(request).subscribe({
      next: () => {
        this.closeTopUpModal();
        this.loadWalletData();
      }
    }));
  }
  // Withdraw from Wallet
  openWithdrawModal() {
    this.showWithdrawModal = true;
    this.withdrawForm = {
      amount: 0,
      destinationAccountId: '',
      pin: ''
    };
  }
  closeWithdrawModal() {
    this.showWithdrawModal = false;
  }
  withdrawFromWallet() {
    if (this.withdrawForm.amount <= 0 || !this.withdrawForm.destinationAccountId) {
      return;
    }
    const request = {
      walletId: this.wallet.id,
